mapper.select:
SELECT dev.id,dev.machine_no machineNo,dev.dept_id,dev.`NAME` deptName, SUM(dev.count) welderNum,dev.welderNo,SUM(dev.count2) taskCount,dev.taskNo, CONCAT(FLOOR(SUM(dev.onOffTime)/3600),':',FLOOR(SUM(dev.onOffTime)%3600/60),':',FLOOR(SUM(dev.onOffTime)%3600%60)) onOffTime, CONCAT(FLOOR(SUM(dev.realWeldTime)/3600),':',FLOOR(SUM(dev.realWeldTime)%3600/60),':',FLOOR(SUM(dev.realWeldTime)%3600%60)) realWeldTime, CONCAT(FLOOR(SUM(dev.normalTime)/3600),':',FLOOR(SUM(dev.normalTime)%3600/60),':',FLOOR(SUM(dev.normalTime)%3600%60)) normalTime, CONCAT(FLOOR(SUM(dev.supergageTime)/3600),':',FLOOR(SUM(dev.supergageTime)%3600/60),':',FLOOR(SUM(dev.supergageTime)%3600%60)) supergageTime, dev.weldingEfficiency*100 weldingEfficiency, dev.standardPercentage*100 standardPercentage, dev.materialsConsumption, dev.powerConsumption powerConsumption FROM(SELECT m.id,m.machine_no,m.dept_id,d.NAME,COUNT(DISTINCT s.welderId) count, GROUP_CONCAT(DISTINCT s.welderNo) welderNo,COUNT(DISTINCT s.taskId) count2, GROUP_CONCAT(DISTINCT s.taskNo) taskNo, SUM(s.weldDuration) onOffTime, SUM(s1.weldDuration) realWeldTime, SUM(s2.weldDuration) normalTime, SUM(s3.weldDuration) supergageTime, CONVERT (sum(s1.weldDuration)/SUM(s.weldDuration),DECIMAL(10,2)) weldingEfficiency, CONVERT (sum(s2.weldDuration)/SUM(s1.weldDuration),DECIMAL(10,2)) standardPercentage, CONVERT (sum(s.wireFeedRate*s.weldDuration)/600,DECIMAL(10,2)) materialsConsumption, CONVERT (sum((s.electricity*s.voltage*s.weldDuration)/3600/1000)+(s.weldDuration-s1.weldDuration)/36000,DECIMAL(10,2)) powerConsumption FROM machine_weld_info m INNER JOIN sys_dept d ON m.dept_id = d.id LEFT JOIN weld_statistics_data s ON d.id = s.machineDeptId AND m.id = s.machineId AND s.startTime >= #{time1} AND s.endTime <= #{time2} LEFT JOIN weld_statistics_data s1 ON s.id = s1.id AND s1.weldStatus IN (3,5,7,99) LEFT JOIN weld_statistics_data s2 ON s1.id = s2.id AND s2.weldStatus IN (3,5,7) LEFT JOIN weld_statistics_data s3 ON s1.id = s3.id AND s3.weldStatus = 99
d.id in
#{id}
and m.machine_no = #{machineNo}
GROUP BY m.machine_no UNION ALL SELECT m.id,m.weld_no machine_no,m.dept_id,d.NAME,COUNT(DISTINCT sx.welderId) count, GROUP_CONCAT(DISTINCT w.welder_no) welderNo,COUNT(DISTINCT sx.taskId) count2, GROUP_CONCAT(DISTINCT sx.taskNo) taskNo, SUM(sx.weldSuration) onOffTime, SUM(sx1.weldSuration) realWeldTime, SUM(sx1.weldSuration) normalTime, 0 supergageTime, CONVERT (SUM(sx2.weldSuration)/SUM(sx1.weldSuration),DECIMAL(10,2)) weldingEfficiency, 100 standardPercentage, CONVERT (sum(sx2.realityWireSpeed*sx2.weldSuration)/600,DECIMAL(10,2)) materialsConsumption, CONVERT (sum((sx1.realityWeldEle*sx1.realityWeldVol*sx1.weldSuration)/3600/1000)+(sx1.weldSuration-sx2.weldSuration)/36000,DECIMAL (10,2)) powerConsumption FROM machine_weldsx_info m LEFT JOIN sys_dept d ON m.dept_id = d.id INNER JOIN weldsx_statistics_data sx ON m.weld_cid = sx.weldCid LEFT JOIN weld_on_off_time ONOFF ON onoff.weld_cid = sx.weldCid LEFT JOIN welder_info w ON w.id = sx.welderId AND sx.startTime >= #{time1} AND sx.endTime <= #{time2} LEFT JOIN weldsx_statistics_data sx1 ON sx.id = sx1.id AND sx1.weldStatus NOT IN (0,3,4,6,9) LEFT JOIN weldsx_statistics_data sx2 ON sx1.id = sx2.id AND sx2.weldStatus NOT IN (3,4,6,9)
d.id in
#{id}
and m.weld_no = #{machineNo}
GROUP BY m.weld_no) dev group by dev.machine_no
