mapper.select:
select m.id,m.gather_no,m.ip_path,m.mac_path,m.production_date, dept.`name` deptName,dic.value_name statusStr,dic1.value_name protocolStr from machine_gather_info m left join sys_dictionary dic on m.status = dic.id left join sys_dictionary dic1 on m.protocol = dic1.id left join sys_dept dept on m.dept_id = dept.id
and m.dept_id = #{grade}
and m.gather_no = #{gatherNo}

mapper.update:
update machine_gather_info
gather_no=#{machineGatherInfo.gatherNo}, dept_id=#{machineGatherInfo.deptId}, status=#{machineGatherInfo.status}, protocol=#{machineGatherInfo.protocol}, create_time=#{machineGatherInfo.createTime}, ip_path = #{machineGatherInfo.ipPath}, mac_path = #{machineGatherInfo.macPath}, last_update_time = NOW()
where id=#{machineGatherInfo.id}
