mapper.select:
select if(welder_info.welder_name is null,'此任务暂未分配',welder_info.welder_name) welderName, task.id,task.task_no,dictionaryTemp.value_name gradeStr, dept.name,deptWork.name workSpace,task.plan_starttime,task.plan_endtime, task.reality_starttime,task.reality_endtime, task.evaluate_content,dictionaryTP.value_name evaluate_stars,dictionaryTP.id startId, task.dept_id,task.welder_id,dictionary.value_name status,task.eleMax,task.eleMin,task.volMax,task.volMin from task_info task left join sys_dept dept on dept.id = task.dept_id left join sys_dept deptWork on deptWork.id = dept.parent_id left join sys_dictionary dictionary on dictionary.id = task.status left join sys_dictionary dictionaryTemp on dictionaryTemp.id = task.grade left join sys_dictionary dictionaryTP on dictionaryTP.id = task.evaluate_stars left join welder_info on welder_info.id = task.welder_id
and task.dept_id = #{grade}
and task.status = #{taskStatus}

mapper.update:
update task_info
task_no = #{taskInfo.taskNo}, grade = #{taskInfo.grade}, dept_id = #{taskInfo.deptId}, plan_starttime = #{taskInfo.planStarttime}, plan_endtime = #{taskInfo.planEndtime}, reality_starttime = #{taskInfo.realityStarttime}, reality_endtime = #{taskInfo.realityEndtime}, status = #{taskInfo.status}, welder_id = #{taskInfo.welderId}, eleMax = #{taskInfo.eleMax}, eleMin = #{taskInfo.eleMin}, volMax = #{taskInfo.volMax}, volMin = #{taskInfo.volMin},
where id=#{taskInfo.id}
