mapper.select:
SELECT p.welder_no welderNo,p.welder_name welderName,p.machine_no machineNo,p.task_no taskNo,p.`NAME` deptName, p.reality_starttime startTime,p.reality_endtime endTime,p.eleScope eleScope, SUM(p.electricity)/2 electricity,p.volScope volScope,SUM(p.voltage)/2 voltage,p.task_id,p.weld_id,p.welder_id FROM(SELECT w.welder_no,w.welder_name,m.machine_no,t.task_no,d.NAME,t.reality_starttime,t.reality_endtime, CONCAT( t.eleMin, '~', t.eleMax ) eleScope, CONVERT (AVG(s.electricity),DECIMAL (10,2)) electricity,CONCAT(t.volMin,'~',t.volMax) volScope, CONVERT (AVG(s.voltage),DECIMAL (10,2)) voltage,c.weld_id,c.welder_id,c.task_id FROM task_claim c INNER JOIN welder_info w ON c.welder_id = w.id INNER JOIN machine_weld_info m ON c.weld_id = m.id INNER JOIN task_info t ON c.task_id = t.id AND t.reality_starttime >= #{time1} AND t.reality_starttime <= #{time2} LEFT JOIN sys_dept d ON d.id = t.dept_id LEFT JOIN weld_statistics_data s ON c.task_id = s.taskId AND c.weld_id = s.machineId AND c.welder_id = s.welderId AND s.weldStatus IN (3,99)
d.id in
#{id}
w.welder_no = #{welderNo}
and w.welder_name = #{welderName}
and m.machine_no = #{machineNo}
and t.task_no = #{taskNo}
GROUP BY c.task_id,c.weld_id,c.welder_id UNION ALL SELECT w.welder_no,w.welder_name,m.weld_cid machine_no,t.task_no,d.NAME,t.reality_starttime,t.reality_endtime, CONCAT( t.eleMin, '~', t.eleMax ) eleScope, CONVERT (AVG(sx.realityWeldEle),DECIMAL (10,2)) electricity,CONCAT(t.volMin,'~',t.volMax) volScope, CONVERT (AVG(sx.realityWeldVol),DECIMAL (10,2)) voltage,c.weld_id,c.welder_id,c.task_id FROM task_claim c INNER JOIN welder_info w ON c.welder_id = w.id INNER JOIN machine_weldsx_info m ON c.weld_id = m.id INNER JOIN task_info t ON c.task_id = t.id AND t.reality_starttime >= #{time1} AND t.reality_starttime <= #{time2} LEFT JOIN sys_dept d ON d.id = t.dept_id LEFT JOIN weldsx_statistics_data sx ON c.task_id = sx.taskId AND c.welder_id = sx.welderId AND sx.weldStatus NOT IN (0,3,4,6,9)
d.id in
#{id}
w.welder_no = #{welderNo}
and w.welder_name = #{welderName}
and m.weld_no = #{machineNo}
and t.task_no = #{taskNo}
GROUP BY c.task_id,c.weld_id,c.welder_id) p group by p.task_id,p.weld_id,p.welder_id
