mapper.select:
SELECT g.id,g.deptName,SUM(g.allCount) allCount,SUM(g.onOffCount) onOffCount, CONCAT(FLOOR(SUM(g.onOffTime)/3600),':',FLOOR(SUM(g.onOffTime)%3600/60),':',FLOOR(SUM(g.onOffTime)%3600%60)) onOffTime, SUM(g.taskCount) taskCount,SUM(g.realWeldOnline) realWeldOnline, CONCAT(FLOOR(SUM(g.realWeldTime)/3600),':',FLOOR(SUM(g.realWeldTime)%3600/60),':',FLOOR(SUM(g.realWeldTime)%3600%60)) realWeldTime, SUM(g.noTaskCount) noTaskCount,g.noTaskMachineDetail, SUM(g.equipUtilization) equipUtilization, CONCAT(FLOOR(SUM(g.supergageTime)/3600),':',FLOOR(SUM(g.supergageTime)%3600/60),':',FLOOR(SUM(g.supergageTime)%3600%60)) supergageTime, CONCAT(FLOOR((SUM(g.normalTime))/3600),':',FLOOR((SUM(g.normalTime))%3600/60),':',FLOOR((SUM(g.normalTime))%3600%60)) normalTime, SUM(g.weldingEfficiency) weldingEfficiency,SUM(g.standardPercentage) standardPercentage,SUM(g.materialsConsumption) materialsConsumption, SUM(g.powerConsumption) powerConsumption FROM(SELECT d.id,d.`name` deptName,COUNT(DISTINCT m.id) allCount,COUNT(DISTINCT s.machineId) onOffCount,SUM(s.weldDuration) onOffTime, COUNT(DISTINCT s.taskId)-COUNT(DISTINCT s2.taskId) taskCount,COUNT(DISTINCT s1.machineId) realWeldOnline,SUM(s1.weldDuration) realWeldTime, IF(SUM(s1.weldDuration)>300,COUNT(DISTINCT s2.machineId),0) noTaskCount,GROUP_CONCAT(distinct s2.machineNo) noTaskMachineDetail, ROUND(COUNT(DISTINCT s.machineId)/COUNT(DISTINCT m.id),2)*100 equipUtilization,SUM(s3.weldDuration) supergageTime, SUM(s1.weldDuration)-IFNULL(SUM(s3.weldDuration),0) normalTime,ROUND((SUM(s1.weldDuration))/SUM(s.weldDuration),2)*100 weldingEfficiency, ROUND((SUM(s1.weldDuration)-IFNULL(SUM(s3.weldDuration),0))/SUM(s1.weldDuration),2)*100 standardPercentage, sum(s.wireFeedRate*s.weldDuration)/600 materialsConsumption, sum((s.electricity*s.voltage*s.weldDuration)/3600/1000)+sum(s.weldDuration-s1.weldDuration)/36000 powerConsumption FROM sys_dept d INNER JOIN machine_weld_info m ON d.id=m.dept_id LEFT JOIN weld_statistics_data s ON d.id=s.machineDeptId AND m.id=s.machineId AND s.startTime >= #{time1} and s.endTime <= #{time2} LEFT JOIN weld_statistics_data s1 ON s.id=s1.id AND s1.weldStatus IN (3,5,7,99) LEFT JOIN weld_statistics_data s3 ON s1.id=s3.id AND s1.weldStatus=99 LEFT JOIN weld_statistics_data s2 ON s1.id=s2.id AND s2.taskId is NULL where d.id in
#{id}
GROUP BY d.`name` UNION ALL SELECT d.id,d.`name` deptName, COUNT(DISTINCT m.id) allCount, COUNT(DISTINCT sx1.weldCid) onOffCount, SUM(sx.weldSuration) onOffTime, COUNT(DISTINCT sx1.taskId)-COUNT(DISTINCT sx2.taskId) taskCount, COUNT(DISTINCT sx3.weldCid) realWeldOnline, SUM(sx3.weldSuration) realWeldTime, IF(SUM(sx3.weldSuration)>300,COUNT(DISTINCT sx2.weldCid),0) noTaskCount, GROUP_CONCAT(distinct sx2.weldCid) noTaskMachineDetail, ROUND(COUNT(DISTINCT sx.weldCid)/COUNT(DISTINCT m.id),2) equipUtilization, 0 supergageTime, SUM(sx3.weldSuration) normalTime, ROUND((SUM(sx1.weldSuration))/SUM(sx3.weldSuration),2) weldingEfficiency, 100 standardPercentage, sum(sx3.realityWireSpeed*sx3.weldSuration)/600 materialsConsumption, sum((sx3.realityWeldEle*sx3.realityWeldVol*sx3.weldSuration)/3600/1000)+(sx3.weldSuration-sx1.weldSuration)/36000 powerConsumption FROM sys_dept d INNER JOIN machine_weldsx_info m ON d.id = m.dept_id LEFT JOIN weldsx_statistics_data sx ON m.weld_cid = sx.weldCid AND sx.startTime >= #{time1} AND sx.endTime <= #{time2} LEFT JOIN weldsx_statistics_data sx1 ON sx.id=sx1.id AND sx1.weldStatus NOT IN (3,4,6,9) LEFT JOIN weldsx_statistics_data sx2 ON sx1.id=sx2.id AND sx2.taskId IS NULL LEFT JOIN weldsx_statistics_data sx3 ON sx1.id=sx3.id AND sx3.weldStatus NOT IN (0,3,4,6,9) WHERE d.id IN
#{id}
GROUP BY d.`name`)g GROUP BY g.deptName
